import React, { useState, useEffect } from 'react';

export default function Tutorial(props){
    const [step,setStep] = useState(0)
    const maxStep = 2

    function stopTutorial(){
        props.onFinish()
        //localStorage.setItem('tutorial',1)
    }
    function nextStep(){
        setStep(step+1)
    }

    const welcome = ()=>{
        window.onload += ()=> {
            console.log('here')
            const textarea = document.getElementById('input-txt').disabled = true
            const button = document.getElementById('input-btn').disabled = true}
        return(
            <div>
              <p>Czy czy chcesz tutorial?</p>
              <button onClick={stopTutorial}>nie</button><button onClick={nextStep}>tak</button>
            </div>
        )
    }
    const step1 =()=>{
        console.log('step')
        props.other.input('test')
        return(<div>
            hello
        </div>)
    }
    const tutorialSteps=[
        welcome,step1
    ]
    function runTutorial(){
        if(props.shouldRun){
            return tutorialSteps[step]()
        }


    }
    useEffect(() => {
        document.getElementById('tutorial').style.display = "block"
    });   
    return(
        <div className='tutorial' id='tutorial'>
                {runTutorial()}
                <div className='clearfix'></div>
        </div>
    )
}
