import React, { useState, useEffect } from 'react';

export default function Tutorial(props){
    const [step,setStep] = useState(0)
    const [text,setText] = useState('')
    const maxStep = 2
    const style =[
        {

        },
        {

        },
        {},
    ]

    function stopTutorial(){
        props.onFinish()
        //localStorage.setItem('tutorial',1)
    }
    function nextStep(){
        setStep(step+1)
        localStorage.setItem('tutorial',1)
    }

    const welcome = ()=>{
        setText('Czy czy chcesz tutorial?')
        localStorage.setItem('tutorial',2)
    }
    const step1 =()=>{
        props.other.input('test')
        setText('hrllo')
        document.getElementById('a').textContent='zamknij'
        document.getElementById('b').textContent='dalej'
    }
    const tutorialSteps=[
        welcome,step1
    ]
    function runTutorial(){
        if(props.shouldRun){
            tutorialSteps[step]()
        }


    }

    useEffect(() => {
        document.getElementById('tutorial').style.display = "block"
        if (!/\d*/g.test(localStorage.getItem('tutorial'))){         
            //jeśli nie liczba, to ustaw 0, tutorial przejdzie od początku
            localStorage.setItem('tutorial',0)
        }
        else{
            //jeśli liczba, tutorial jest w trakcie, kontunuuj od poprzedniego kroku
            //setStep(parseInt(localStorage.getItem('tutorial'))) bo  jest out of bounds
        }
        console.log(step)
        runTutorial()
    });   
    return(
        <div className='tutorial' id='tutorial'>
               <p>{text}</p>

               <button id={'a'} onClick={stopTutorial}>nie</button><button id={'b'} onClick={nextStep}>tak</button>
                <div className='clearfix'></div>

        </div>
    )
}
