    const maxStep = 2

    function stopTutorial(){
        setShouldRun(false)
    }
    function nextStep(){
        setStep(step+1)
    }

    const welcome = ()=>{
        console.log('welcome')
        return(
            <div>
              <p>Czy czy chcesz tutorial?</p>

            </div>
        )
    }
    const step1 =()=>{
        console.log('step')
        return(<div>
            hello
        </div>)
    }
    const tutorialSteps=[
        welcome,step1
    ]
    function runTutorial(){
        if(shouldRun){
            console.log('here')
            tutorialSteps[step]()
        }
    }
